mapper.update:

update tbl_mark
set mark_name = #{markName,jdbcType=VARCHAR},
mark_status = #{markStatus,jdbcType=CHAR},
mark_ctime = #{markCtime,jdbcType=TIMESTAMP}
where mark_id = #{markId,jdbcType=INTEGER}

mapper.select:

select mark_id, mark_name, mark_status, mark_ctime
from tbl_mark
where mark_id = #{markId,jdbcType=INTEGER}
